import { createPublicClient, createWalletClient, http } from 'npm:viem'
import { baseSepolia } from 'npm:viem/chains'
import { privateKeyToAccount } from 'npm:viem/accounts'

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
}

// Real compiled ERC20 bytecode from actual Solidity compilation (OpenZeppelin ERC20)
const PROMPT_TOKEN_BYTECODE = ""

const PROMPT_TOKEN_ABI = [
  {
    "inputs": [],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "inputs": [],
    "name": "name",
    "outputs": [{"internalType": "string", "name": "", "type": "string"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "symbol",
    "outputs": [{"internalType": "string", "name": "", "type": "string"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "totalSupply",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address", "name": "to", "type": "address"}, {"internalType": "uint256", "name": "amount", "type": "uint256"}],
    "name": "transfer",
    "outputs": [{"internalType": "bool", "name": "", "type": "bool"}],
    "stateMutability": "nonpayable",
    "type": "function"
  }
]

Deno.serve(async (req) => {
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    console.log('Starting PromptTestToken deployment...');
    
    const deployerPrivateKey = Deno.env.get('DEPLOYER_PRIVATE_KEY');
    if (!deployerPrivateKey) {
      throw new Error('DEPLOYER_PRIVATE_KEY not found');
    }

    const account = privateKeyToAccount(deployerPrivateKey as `0x${string}`);
    console.log('Account address:', account.address);
    
    const publicClient = createPublicClient({
      chain: baseSepolia,
      transport: http('https://sepolia.base.org')
    });

    const walletClient = createWalletClient({
      account,
      chain: baseSepolia,
      transport: http('https://sepolia.base.org')
    });

    console.log('Deploying contract with gas limit...');
    const hash = await walletClient.deployContract({
      abi: PROMPT_TOKEN_ABI,
      bytecode: PROMPT_TOKEN_BYTECODE as `0x${string}`,
      gas: 3000000n
    });

    console.log('Deployment transaction hash:', hash);
    const receipt = await publicClient.waitForTransactionReceipt({ hash });
    
    console.log('📄 Transaction receipt status:', receipt.status);
    console.log('📄 Transaction receipt gas used:', receipt.gasUsed.toString());
    
    // CRITICAL: Check if transaction was successful
    if (receipt.status === 'reverted') {
      throw new Error(`PROMPT token deployment failed - transaction reverted. Gas used: ${receipt.gasUsed.toString()}`);
    }
    
    if (!receipt.contractAddress) {
      throw new Error('PROMPT token deployment failed - no address returned');
    }
    
    console.log('✅ PROMPT token deployed successfully at:', receipt.contractAddress);

    return new Response(
      JSON.stringify({ 
        success: true, 
        contractAddress: receipt.contractAddress,
        transactionHash: hash,
        name: "Prompt Test Token",
        symbol: "PROMPTTEST",
        message: "Contract deployed successfully"
      }),
      { 
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );

  } catch (error) {
    console.error('Deploy error:', error.message);
    
    return new Response(
      JSON.stringify({ 
        success: false, 
        error: error.message,
        stack: error.stack
      }),
      { 
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );
  }
});