import { createPublicClient, createWalletClient, http } from 'npm:viem'
import { baseSepolia } from 'npm:viem/chains'
import { privateKeyToAccount } from 'npm:viem/accounts'
import { verifyDeployment } from '../_shared/verifyDeployment.ts'

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
}

// Compiled PromptTestToken bytecode (with proper constructor)
const PROMPT_TEST_TOKEN_BYTECODE = ""

const PROMPT_TEST_TOKEN_ABI = [
  {
    "inputs": [],
    "stateMutability": "nonpayable",
    "type": "constructor"
  },
  {
    "inputs": [],
    "name": "name",
    "outputs": [{"internalType": "string", "name": "", "type": "string"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "symbol",
    "outputs": [{"internalType": "string", "name": "", "type": "string"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "decimals",
    "outputs": [{"internalType": "uint8", "name": "", "type": "uint8"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "totalSupply",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [{"internalType": "address", "name": "account", "type": "address"}],
    "name": "balanceOf",
    "outputs": [{"internalType": "uint256", "name": "", "type": "uint256"}],
    "stateMutability": "view",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address", "name": "to", "type": "address"},
      {"internalType": "uint256", "name": "amount", "type": "uint256"}
    ],
    "name": "transfer",
    "outputs": [{"internalType": "bool", "name": "", "type": "bool"}],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [],
    "name": "faucet",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  },
  {
    "inputs": [
      {"internalType": "address", "name": "to", "type": "address"},
      {"internalType": "uint256", "name": "amount", "type": "uint256"}
    ],
    "name": "mint",
    "outputs": [],
    "stateMutability": "nonpayable",
    "type": "function"
  }
]

Deno.serve(async (req) => {
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  try {
    console.log('ğŸš€ Deploying PromptTestToken contract...');
    
    const deployerPrivateKey = Deno.env.get('DEPLOYER_PRIVATE_KEY');
    if (!deployerPrivateKey) {
      throw new Error('DEPLOYER_PRIVATE_KEY not found in environment');
    }

    // Ensure private key has 0x prefix
    const formattedPrivateKey = deployerPrivateKey.startsWith('0x') 
      ? deployerPrivateKey 
      : `0x${deployerPrivateKey}`;
    
    console.log('ğŸ”‘ Private key format validated');
    console.log('ğŸ“ Private key length:', formattedPrivateKey.length);

    const account = privateKeyToAccount(formattedPrivateKey as `0x${string}`);
    
    const publicClient = createPublicClient({
      chain: baseSepolia,
      transport: http()
    });

    const walletClient = createWalletClient({
      account,
      chain: baseSepolia,
      transport: http()
    });

    console.log('ğŸ’¼ Deployer address:', account.address);
    
    // Check wallet balance
    const balance = await publicClient.getBalance({
      address: account.address
    });
    console.log('ğŸ’° Deployer balance:', balance.toString(), 'wei');
    
    if (balance === 0n) {
      throw new Error('âŒ Deployer wallet has no ETH for gas fees');
    }

    // Get current gas price for logging
    const gasPrice = await publicClient.getGasPrice();
    console.log('â›½ Current gas price:', gasPrice.toString(), 'wei');

    // Deploy the contract with a reasonable gas limit
    console.log('ğŸ”„ Deploying contract...');
    const hash = await walletClient.deployContract({
      abi: PROMPT_TEST_TOKEN_ABI,
      bytecode: PROMPT_TEST_TOKEN_BYTECODE as `0x${string}`,
      gas: 3000000n, // Fixed gas limit instead of estimation
    });

    console.log('ğŸ“ Transaction hash:', hash);

    // Wait for transaction receipt
    console.log('â³ Waiting for transaction confirmation...');
    const receipt = await publicClient.waitForTransactionReceipt({ 
      hash,
      timeout: 60000 // 60 second timeout
    });
    
    console.log('âœ… PromptTestToken deployed at:', receipt.contractAddress);
    console.log('ğŸ§¾ Gas used:', receipt.gasUsed.toString());

    if (!receipt.contractAddress) {
      throw new Error('âŒ Contract deployment failed - no address returned');
    }

    // ğŸ”’ MANDATORY VERIFICATION: Ensure contract exists on-chain before proceeding
    console.log('ğŸ” Verifying PromptTestToken deployment before database operations...');
    const verification = await verifyDeployment(receipt.contractAddress, publicClient, 'prompt_test_token');
    console.log('âœ… Contract verification passed:', verification);

    return new Response(
      JSON.stringify({ 
        success: true, 
        contractAddress: receipt.contractAddress,
        transactionHash: hash,
        name: "Prompt Test Token",
        symbol: "PROMPTTEST",
        decimals: 18,
        totalSupply: "1000000000000000000000000", // 1M tokens
        gasUsed: receipt.gasUsed.toString(),
        blockNumber: receipt.blockNumber.toString()
      }),
      { 
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );

  } catch (error) {
    console.error('âŒ PromptTestToken deployment error:', error);
    
    return new Response(
      JSON.stringify({ 
        success: false, 
        error: error.message,
        details: error.details || 'No additional details available'
      }),
      { 
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );
  }
});